class CMeshField : public CSceneMesh
{
public:
	CMeshField(LPDIRECT3DDEVICE9 pDevice,int nPriority = 3);
	~CMeshField();

	HRESULT Init(void);
	void Uninit(void);
	void Update(void);
	void Draw(void);

	static CMeshField* Create(D3DXVECTOR3 pos,D3DXVECTOR3 rot,int nNumBlockX,int nNumBlockZ,float fSizeBlockX,float fSizeBlockZ,
									LPDIRECT3DDEVICE9 pDevice,int nPriority , const char* pFileName = "data/TEXTURE/grass.jpg");

	D3DXVECTOR3* GetConers(void){ return m_aPosCorners; }

	float GetHeight(const D3DXVECTOR3& pos, D3DXVECTOR3* normal);
	void SetShadowTexture(LPDIRECT3DTEXTURE9 ptex){ m_pShadowTexture = ptex; }
	void SetPlayerPos(D3DXVECTOR3 pos){ m_playerPos = pos; }

private:
	float Vec2Cross(const D3DXVECTOR2& vec1, const D3DXVECTOR2& vec2){ return ((vec1.x * vec2.y) - (vec1.y * vec2.x)); }
	float GetHeightPolygon(const D3DXVECTOR3& p0, const D3DXVECTOR3& p1, const D3DXVECTOR3& p2, const D3DXVECTOR3& pos, D3DXVECTOR3* pNormal);
	void SetBuffer(void);
	float m_fSizeBlockX;
	float m_fSizeBlockZ;
	int m_nNumBlockX;
	int m_nNumBlockZ;
	D3DXVECTOR3 m_posLeftFront;
	D3DXVECTOR3 m_posRightFront;
	D3DXVECTOR3 m_posLeftBack;
	D3DXVECTOR3 m_posRightBack;
	D3DXVECTOR3 m_aPosCorners[4];
	D3DXVECTOR3 m_normalBuf[50 * 50 * 2];

	D3DXVECTOR3 m_posBuff[51 * 51];

	CShadowShader* m_pShadowShader;
	LPDIRECT3DVERTEXDECLARATION9 m_pDec;
	const char* m_pFileName;

	LPDIRECT3DTEXTURE9 m_pShadowTexture;
	D3DXVECTOR3 m_playerPos;
};
